import cv2
import numpy as np
from matplotlib import pyplot as plt

# Load image and convert to grayscale
noisy_image = cv2.imread('noisy_image.jpg', cv2.IMREAD_GRAYSCALE)
plt.imshow(noisy_image, cmap='gray')

"""![image.png](data:image/png;base64,)

#Midpoint Filter Implementation
"""

def midpoint_filter(image, kernel_size=3):
    pad = kernel_size // 2
    padded = cv2.copyMakeBorder(image, pad, pad, pad, pad, cv2.BORDER_REFLECT)
    result = np.zeros_like(image)

    for i in range(pad, padded.shape[0]-pad):
        for j in range(pad, padded.shape[1]-pad):
            neighborhood = padded[i-pad:i+pad+1, j-pad:j+pad+1]
            max_val = np.max(neighborhood)
            min_val = np.min(neighborhood)
            result[i-pad, j-pad] = (max_val + min_val) // 2

    return result

midpoint_result = midpoint_filter(noisy_image)

#visualization
plt.subplot(232), plt.imshow(noisy_image, cmap='gray'), plt.title('Noisy Image')
plt.subplot(233), plt.imshow(midpoint_result, cmap='gray'), plt.title('Midpoint Filter')

plt.tight_layout()
plt.show()

"""#Alpha-trimmed Mean Filter Implementation"""

def alpha_trimmed_mean_filter(image, kernel_size=3, d=2):
    pad = kernel_size // 2
    padded = cv2.copyMakeBorder(image, pad, pad, pad, pad, cv2.BORDER_REFLECT)
    result = np.zeros_like(image)

    for i in range(pad, padded.shape[0]-pad):
        for j in range(pad, padded.shape[1]-pad):
            neighborhood = padded[i-pad:i+pad+1, j-pad:j+pad+1].flatten()
            neighborhood_sorted = np.sort(neighborhood)
            trimmed = neighborhood_sorted[d//2:-d//2] if d > 0 else neighborhood_sorted
            result[i-pad, j-pad] = np.mean(trimmed)

    return result

alpha_trimmed_result = alpha_trimmed_mean_filter(noisy_image)

#visualization
plt.subplot(232), plt.imshow(noisy_image, cmap='gray'), plt.title('Noisy Image')
plt.subplot(233), plt.imshow(alpha_trimmed_result, cmap='gray'), plt.title('Alpha-trimmed Mean')

plt.tight_layout()
plt.show()

"""#Arithmetic Mean Filter Implementation"""

def arithmetic_mean_filter(image, kernel_size=3):
    kernel = np.ones((kernel_size, kernel_size), np.float32) / (kernel_size**2)
    return cv2.filter2D(image, -1, kernel)

arithmetic_mean_result = arithmetic_mean_filter(noisy_image)

#visualization
plt.subplot(232), plt.imshow(noisy_image, cmap='gray'), plt.title('Noisy Image')
plt.subplot(233), plt.imshow(arithmetic_mean_result, cmap='gray'), plt.title('Arithmetic Mean')

plt.tight_layout()
plt.show()

"""#Contraharmonic Mean Filter Implementation

Contraharmonic Mean Filter can be used for one purpose at a time either for removing salt noise or for removing pepper noise by controlling parameter Q from negative to positive values but cannot be applied for both purposes at the same time.

#Here it is used for removing Pepper Noise by setting Q to a positive value greater than 0.
"""

def contraharmonic_mean_filter(image, kernel_size=3, Q=1.5):
    pad = kernel_size // 2
    padded = cv2.copyMakeBorder(image, pad, pad, pad, pad, cv2.BORDER_REFLECT)
    result = np.zeros_like(image, dtype=np.float32)

    for i in range(pad, padded.shape[0]-pad):
        for j in range(pad, padded.shape[1]-pad):
            neighborhood = padded[i-pad:i+pad+1, j-pad:j+pad+1].astype(np.float32)
            numerator = np.sum(neighborhood ** (Q + 1))
            denominator = np.sum(neighborhood ** Q)
            if denominator != 0:
                result[i-pad, j-pad] = numerator / denominator
            else:
                result[i-pad, j-pad] = 0

    return np.clip(result, 0, 255).astype(np.uint8)

contraharmonic_result = contraharmonic_mean_filter(noisy_image)


#visualization
plt.subplot(232), plt.imshow(noisy_image, cmap='gray'), plt.title('Noisy Image')
plt.subplot(233), plt.imshow(contraharmonic_result, cmap='gray'), plt.title('Contraharmonic Mean')

plt.tight_layout()
plt.show()

"""![image.png](data:image/png;base64,)

#Modify Contraharmonic Mean Filter to Remove Salt Noise

To remove salt noise (bright spots), use negative Q value

#Using Q = -1
"""

# To remove salt noise (bright spots), use negative Q value
contraharmonic_salt_removal = contraharmonic_mean_filter(noisy_image, Q=-1)

plt.figure(figsize=(10, 5))
plt.subplot(121), plt.imshow(noisy_image, cmap='gray'), plt.title('Noisy Image')
plt.subplot(122), plt.imshow(contraharmonic_salt_removal, cmap='gray'), plt.title('Salt Noise Removal (Q=-1)')
plt.show()

