import cv2
import matplotlib.pyplot as plt
import numpy as np

image = cv2.imread('PlayTennis.jpg', cv2.IMREAD_GRAYSCALE)
kernel = cv2.getStructuringElement(cv2.MORPH_RECT, (5,5))
plt.imshow(image, cmap='gray')

"""# 1. Read image and create kernel

"""

print("Kernel for Morphological Transformations:\n", kernel)

"""![image.png](data:image/png;base64,)

# 2. Erosion with 3 iterations
"""

plt.figure(figsize=(15, 10))
for i, iterations in enumerate([1, 2, 3], 1):
    erosion = cv2.erode(image, kernel, iterations=iterations)
    plt.subplot(3, 3, i)
    plt.imshow(erosion, cmap='gray')
    plt.title(f'Erosion (iter={iterations})')

"""![image.png](data:image/png;base64,)

![image.png](data:image/png;base64,)

# 3. Dilation with 3 iterations
"""

plt.figure(figsize=(15, 10))
for i, iterations in enumerate([1, 2, 3], 4):
    dilation = cv2.dilate(image, kernel, iterations=iterations)
    plt.subplot(3, 3, i)
    plt.imshow(dilation, cmap='gray')
    plt.title(f'Dilation (iter={iterations})')

"""![image.png](data:image/png;base64,)

![image.png](data:image/png;base64,)

# 4. Opening (Erosion->Dilation) with 3 iterations
"""

plt.figure(figsize=(15, 10))
for i, iterations in enumerate([1, 2, 3], 7):
    opening = cv2.morphologyEx(image, cv2.MORPH_OPEN, kernel, iterations=iterations)
    plt.subplot(3, 3, i)
    plt.imshow(opening, cmap='gray')
    plt.title(f'Opening (iter={iterations})')

plt.tight_layout()
plt.show()

"""![image.png](data:image/png;base64,)

![image.png](data:image/png;base64,)

# 5. Closing (Dilation->Erosion) with 3 iterations
"""

plt.figure(figsize=(15, 5))
for i, iterations in enumerate([1, 2, 3], 1):
    closing = cv2.morphologyEx(image, cv2.MORPH_CLOSE, kernel, iterations=iterations)
    plt.subplot(1, 3, i)
    plt.imshow(closing, cmap='gray')
    plt.title(f'Closing (iter={iterations})')
plt.tight_layout()
plt.show()

